from flask import Flask, jsonifyapp = Flask(__name__)@app.route('/add', methods=['POST'])def add():    return jsonify({"message": "Results saved to database."})@app.route('/results', methods=['GET'])def get_results():    results = "Getting results from the database."    return jsonify(results)@app.route('/delete', methods=['DELETE'])def delete():    return jsonify({"message": "All results deleted."})@app.route('/update', methods=['PUT'])def update():    return jsonify({"message": f"Result have been updated."})if __name__ == '__main__':    app.run(debug=True)